craft.script:
function main(params){
    var angle = params.angle
    var part;

console.log(angle)

    if(angle === 30)
        part = thirty()
    else if(angle === 45)
        part = fourtyfive()
    else if(angle === 60)
        part = sixty()
    else if(angle === 90)
        part = ninety()
    else if(angle === 120)
        part = onetwenty()
    else if(angle === 180)
        part = oneeighty()

    var pie = difference(
        cylinder({r:20, h:3, fn:100})
        ,part
    )
    return union(
        pie,
        part.translate([1,-1,0])
    );
}

function oneeighty(){
    return union(
        ninety(),
        ninety().rotateZ(90)
    );
}

function onetwenty(){

    return union(
        ninety().rotateZ(30),
        thirty()
        );
}

function ninety(){
    return union(
        sixty().rotateZ(30),
        thirty()
    );
}

function thirty(){

    return difference(
        sixty(),
        sixty().rotateZ(30).setColor([1,0,0])
    );
}

function fourtyfive(){

    return union(
         thirty(),
         thirty().rotateZ(15)
    );
}
function sixty() {

    var angle = 60
        ,iter = Math.floor(360/angle)

    var pie = cylinder({r:20, h:3, fn:100}).setColor([0,1,1])
        ,block = cube([40,40,3]).translate([-20,0,0])


    for(var i=0; i<iter-1; i++){
        //block = block.rotateZ(angle)
        angle = angle*(i+1)
        pie = difference(
                pie,
                block.rotateZ(angle)
            )
    }

    return pie;
}
